// frontend/src/components/Marketplace.js

import React, { useEffect, useState } from 'react';

function Marketplace() {
  const [credits, setCredits] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/list_credits')
      .then(res => res.json())
      .then(data => setCredits(data));
  }, []);

  const handleBuy = (creditId) => {
    alert(`Simulated purchase of credit ${creditId}`);
    // In a real app, you would call a backend endpoint to transfer ownership
  };

  return (
    <div>
      <h2>Marketplace</h2>
      <ul>
        {credits.map(credit => (
          <li key={credit.credit_id}>
            Credit ID: {credit.credit_id}, Project ID: {credit.project_id}, Amount: {credit.amount}, Owner: {credit.owner}
            <button onClick={() => handleBuy(credit.credit_id)} style={{ marginLeft: '10px' }}>
              Buy
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Marketplace;
