import React, { useEffect, useState } from 'react';

function BoardVerify({ userEmail, setRefresh, refresh }) {
  const [projects, setProjects] = useState([]);
  const [credits, setCredits] = useState([]);

  const fetchData = () => {
    fetch('http://localhost:5000/projects')
      .then(res => res.json())
      .then(data => setProjects(data));
    fetch('http://localhost:5000/list_credits')
      .then(res => res.json())
      .then(data => setCredits(data));
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line
  }, [refresh]);

  const handleVerifyProject = (projectId) => {
    fetch('http://localhost:5000/verify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ project_id: projectId, verifier_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'verified') {
          alert('Project verified!');
          setRefresh(r => r + 1);
        } else {
          alert(data.message || 'Verification failed');
        }
      });
  };

  const handleVerifyCredit = (creditId) => {
    fetch('http://localhost:5000/verify_credit', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ credit_id: creditId, verifier_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'verified') {
          alert('Credit verified!');
          setRefresh(r => r + 1);
        } else {
          alert(data.message || 'Credit verification failed');
        }
      });
  };

  return (
    <div>
      <h2>Verify Projects</h2>
      <ul>
        {projects.filter(p => p.status !== 'verified').map(project => (
          <li key={project.project_id}>
            {project.name} (ID: {project.project_id}) - Owner: {project.owner_email}
            <button onClick={() => handleVerifyProject(project.project_id)} style={{ marginLeft: '10px' }}>
              Verify Project
            </button>
          </li>
        ))}
      </ul>
      <h2>Verify Credits</h2>
      <ul>
        {credits.filter(c => c.status === 'issued').map(credit => (
          <li key={credit.credit_id}>
            Credit ID: {credit.credit_id}, Project ID: {credit.project_id}, Amount: {credit.amount}, Issuer: {credit.issuer_email}
            <button onClick={() => handleVerifyCredit(credit.credit_id)} style={{ marginLeft: '10px' }}>
              Verify Credit
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BoardVerify;
