// frontend/src/components/Dashboard.js

import React, { useEffect, useState } from 'react';

function Dashboard() {
  const [projects, setProjects] = useState([]);
  const [credits, setCredits] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/projects')
      .then(res => res.json())
      .then(data => setProjects(data));
    fetch('http://localhost:5000/list_credits')
      .then(res => res.json())
      .then(data => setCredits(data));
  }, []);

  return (
    <div>
      <h2>Dashboard</h2>
      <h3>Registered Projects</h3>
      <ul>
        {projects.map(project => (
          <li key={project.project_id}>
            {project.name} (ID: {project.project_id}) - Status: {project.status}
          </li>
        ))}
      </ul>
      <h3>Issued Credits</h3>
      <ul>
        {credits.map(credit => (
          <li key={credit.credit_id}>
            Credit ID: {credit.credit_id}, Project ID: {credit.project_id}, Amount: {credit.amount}, Owner: {credit.owner}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
