import React, { useState, useEffect } from 'react';
import GoogleLoginButton from './components/GoogleLoginButton';
import Onboarding from './components/Onboarding';
import Dashboard from './components/Dashboard';
import Marketplace from './components/Marketplace';
import BoardVerify from './components/BoardVerify';

import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Link from '@mui/material/Link';
import Box from '@mui/material/Box';

const BOARD_EMAIL = "manoharareddyp97@gmail.com";

function App() {
  const [user, setUser] = useState(null);
  const [refresh, setRefresh] = useState(0);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const email = params.get('user');
    if (email) setUser(email);
  }, []);

  if (!user) {
    return (
      <Box className="signin-container">
        <Typography variant="h2" component="h1" gutterBottom sx={{ fontWeight: 'bold', color: 'white', textAlign: 'center' }}>
          Welcome
        </Typography>
        <Typography variant="h4" component="h2" gutterBottom sx={{ fontWeight: 'medium', color: 'white', textAlign: 'center', mb: 4 }}>
          Carbon Trading Platform
        </Typography>
        <GoogleLoginButton />
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
      </Box>
    );
  }

  if (user === BOARD_EMAIL) {
    return (
      <Container maxWidth="md" sx={{ mt: 4, mb: 4, bgcolor: 'background.paper', borderRadius: 3, boxShadow: 3, p: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Welcome Board,
        </Typography>
        <Typography variant="h6" gutterBottom>
          {user}
        </Typography>
        <BoardVerify userEmail={user} setRefresh={setRefresh} refresh={refresh} />
        <Link href="http://localhost:5000/logout" underline="none" sx={{ mt: 3, display: 'inline-block', color: 'error.main', fontWeight: 'bold' }}>
          Logout
        </Link>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4, mb: 4, bgcolor: 'background.paper', borderRadius: 3, boxShadow: 3, p: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        User : {user}
      </Typography>
      <Onboarding userEmail={user} setRefresh={setRefresh} refresh={refresh} />
      <Dashboard userEmail={user} refresh={refresh} />
      <Marketplace userEmail={user} refresh={refresh} />
      <Link href="http://localhost:5000/logout" underline="none" sx={{ mt: 3, display: 'inline-block', color: 'error.main', fontWeight: 'bold' }}>
        Logout
      </Link>
    </Container>
  );
}

export default App;
