import React, { useState, useEffect } from 'react';
import GoogleLoginButton from './components/GoogleLoginButton';
import Onboarding from './components/Onboarding';
import Dashboard from './components/Dashboard';
import Marketplace from './components/Marketplace';
import BoardVerify from './components/BoardVerify';

const BOARD_EMAIL = "manoharareddyp97@gmail.com";

function App() {
  const [user, setUser] = useState(null);
  const [refresh, setRefresh] = useState(0);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const email = params.get('user');
    if (email) setUser(email);
  }, []);

  if (!user) {
    return (
      <div className="signin-container">
        <svg className="signin-logo" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 64 64" width="120" height="120" fill="green" aria-label="Carbon Trading Logo" role="img">
          <circle cx="32" cy="32" r="30" stroke="green" strokeWidth="4" fill="none" />
          <path d="M20 32c0-6 5-11 12-11s12 5 12 11-5 11-12 11-12-5-12-11z" fill="green" />
          <path d="M32 21v22" stroke="white" strokeWidth="2" />
          <path d="M24 32h16" stroke="white" strokeWidth="2" />
        </svg>
        <h1>Welcome</h1>
        <div className="signin-tagline">Empowering Carbon Trading for a Sustainable Future</div>
        <h2>Carbon Trading Platform</h2>
        <GoogleLoginButton />
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
        <div className="bubble"></div>
      </div>
    );
  }

  if (user === BOARD_EMAIL) {
    return (
      <div className="app-container">
        <h1>Welcome Board, </h1>
        <h2>{user}</h2>
        <BoardVerify userEmail={user} setRefresh={setRefresh} refresh={refresh} />
        <a href="http://localhost:5000/logout" className="logout-link">Logout</a>
      </div>
    );
  }

  return (
    <div className="app-container">
      <h1>User : {user}</h1>
      <Onboarding userEmail={user} setRefresh={setRefresh} refresh={refresh} />
      <Dashboard userEmail={user} refresh={refresh} />
      <Marketplace userEmail={user} refresh={refresh} />
      <a href="http://localhost:5000/logout" className="logout-link">Logout</a>
    </div>
  );
}

export default App;
