import React, { useState } from 'react';

function Onboarding({ setRefresh }) {
  const [name, setName] = useState('');
  const [projectId, setProjectId] = useState(null);

  const handleRegister = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name })
    })
      .then(res => res.json())
      .then(data => {
        setProjectId(data.project_id);
        alert('Project registered! Project ID: ' + data.project_id);
        setRefresh(r => r + 1);
      });
  };

  const handleVerify = () => {
    if (!projectId) return;
    fetch('http://localhost:5000/verify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ project_id: projectId })
    })
      .then(res => res.json())
      .then(data => {
        alert('Project verified!');
        setRefresh(r => r + 1);
      });
  };

  const handleIssueCredit = () => {
    if (!projectId) return;
    fetch('http://localhost:5000/issue_credit', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ project_id: projectId, amount: 10, owner: 'You' })
    })
      .then(res => res.json())
      .then(data => {
        alert('Credit issued!');
        setRefresh(r => r + 1);
      });
  };

  return (
    <div>
      <form onSubmit={handleRegister}>
        <h2>Register Project</h2>
        <input value={name} onChange={e => setName(e.target.value)} placeholder="Project Name" required />
        <button type="submit">Register</button>
      </form>
      {projectId && (
        <div>
          <button onClick={handleVerify}>Verify Project</button>
          <button onClick={handleIssueCredit}>Issue Credit</button>
        </div>
      )}
    </div>
  );
}

export default Onboarding;
