import React, { useState } from 'react';

function Onboarding({ userEmail, setRefresh }) {
  const [name, setName] = useState('');
  const [projectId, setProjectId] = useState(null);
  const [projectStatus, setProjectStatus] = useState(null);

  const handleRegister = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name, user_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        setProjectId(data.project_id);
        setProjectStatus('registered');
        alert('Project registered! Project ID: ' + data.project_id);
        setRefresh(r => r + 1);
      });
  };

  const handleVerify = () => {
    if (!projectId) return;
    fetch('http://localhost:5000/verify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ project_id: projectId, verifier_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'verified') {
          setProjectStatus('verified');
          alert('Project verified!');
        } else {
          alert(data.message || 'Verification failed');
        }
        setRefresh(r => r + 1);
      });
  };

  const handleIssueCredit = () => {
    if (!projectId) return;
    fetch('http://localhost:5000/issue_credit', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ project_id: projectId, amount: 10, owner_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'issued') {
          alert('Credit issued!');
        } else {
          alert(data.message || 'Credit issuance failed');
        }
        setRefresh(r => r + 1);
      });
  };

  return (
    <div>
      <form onSubmit={handleRegister}>
        <h2>Register Project</h2>
        <input value={name} onChange={e => setName(e.target.value)} placeholder="Project Name" required />
        <button type="submit">Register</button>
      </form>
      {projectId && (
        <div>
          <p>Project ID: {projectId} | Status: {projectStatus}</p>
          <button onClick={handleVerify}>Verify Project (Only for Board)</button>
          <button onClick={handleIssueCredit}>Issue Credit (If Verified)</button>
        </div>
      )}
    </div>
  );
}

export default Onboarding;
