import React, { useEffect, useState } from 'react';
import { FaShoppingCart, FaTag, FaTimesCircle } from 'react-icons/fa';

function Marketplace({ userEmail, refresh }) {
  const [ownedCredits, setOwnedCredits] = useState([]);
  const [marketCredits, setMarketCredits] = useState([]);

  const fetchOwnedCredits = () => {
    fetch('http://localhost:5000/list_credits?user_email=' + userEmail)
      .then(res => res.json())
      .then(data => setOwnedCredits(data));
  };

  const fetchMarketCredits = () => {
    fetch('http://localhost:5000/list_credits?user_email=' + userEmail + '&market=true')
      .then(res => res.json())
      .then(data => setMarketCredits(data));
  };

  useEffect(() => {
    fetchOwnedCredits();
    fetchMarketCredits();
  }, [refresh]);

  const handleBuy = (credit) => {
    if (credit.owner_email === userEmail) {
      alert('You already own this credit.');
      return;
    }
    fetch('http://localhost:5000/purchase_credit', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ credit_id: credit.credit_id, buyer_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'success') {
          alert('Purchase successful!');
          fetchOwnedCredits();
          fetchMarketCredits();
        } else {
          alert(data.message || 'Purchase failed');
        }
      });
  };

  const handleSetForSale = (credit) => {
    fetch('http://localhost:5000/set_for_sale', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ credit_id: credit.credit_id, user_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'success') {
          alert('Credit put for sale successfully!');
          fetchOwnedCredits();
          fetchMarketCredits();
        } else {
          alert(data.message || 'Failed to put credit for sale');
        }
      });
  };

  const handleRemoveFromSale = (credit) => {
    fetch('http://localhost:5000/remove_from_sale', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ credit_id: credit.credit_id, user_email: userEmail })
    })
      .then(res => res.json())
      .then(data => {
        if (data.status === 'success') {
          alert('Credit removed from sale successfully!');
          fetchOwnedCredits();
          fetchMarketCredits();
        } else {
          alert(data.message || 'Failed to remove credit from sale');
        }
      });
  };

  return (
    <div className="section">
      <h2>Credits on Market</h2>
      <button onClick={() => { fetchOwnedCredits(); fetchMarketCredits(); }} style={{
        backgroundColor: '#0077ff',
        color: 'white',
        border: 'none',
        padding: '10px 18px',
        borderRadius: '8px',
        cursor: 'pointer',
        fontSize: '16px',
        fontWeight: '600',
        boxShadow: '0 4px 6px rgba(0, 119, 255, 0.4)',
        transition: 'background-color 0.3s ease, transform 0.2s ease',
        marginBottom: '20px'
      }}
      onMouseEnter={e => {
        e.currentTarget.style.backgroundColor = '#005ecb';
        e.currentTarget.style.transform = 'translateY(-2px)';
        e.currentTarget.style.boxShadow = '0 6px 10px rgba(0, 94, 203, 0.6)';
      }}
      onMouseLeave={e => {
        e.currentTarget.style.backgroundColor = '#0077ff';
        e.currentTarget.style.transform = 'translateY(0)';
        e.currentTarget.style.boxShadow = '0 4px 6px rgba(0, 119, 255, 0.4)';
      }}
      >
        Refresh
      </button>
      {ownedCredits.length === 0 && <p>No owned credits found.</p>}
      {ownedCredits.map(credit => (
        <div key={credit.credit_id} className="card credit-info" style={{ position: 'relative' }}>
          <p>Credit ID: {credit.credit_id}, Project ID: {credit.project_id}, Amount: {credit.amount}, Issuer: {credit.issuer_email}, Owner: {credit.owner_email}, Status: {credit.status}</p>
          {!credit.for_sale && (
            <button onClick={() => handleSetForSale(credit)} className="button-margin-left" style={{
              backgroundColor: '#f39c12',
              color: 'white',
              border: 'none',
              padding: '8px 14px',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '600',
              boxShadow: '0 4px 6px rgba(243, 156, 18, 0.4)',
              transition: 'background-color 0.3s ease, transform 0.2s ease',
              display: 'flex',
              alignItems: 'center'
            }}
            onMouseEnter={e => {
              e.currentTarget.style.backgroundColor = '#d68910';
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = '0 6px 10px rgba(214, 137, 16, 0.6)';
            }}
            onMouseLeave={e => {
              e.currentTarget.style.backgroundColor = '#f39c12';
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 4px 6px rgba(243, 156, 18, 0.4)';
            }}
            >
              <FaTag style={{ marginRight: '6px' }} />
              Put for Sale
            </button>
          )}
          {credit.for_sale && (
            <button onClick={() => handleRemoveFromSale(credit)} className="button-margin-left" style={{
              backgroundColor: '#e74c3c',
              color: 'white',
              border: 'none',
              padding: '8px 14px',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '600',
              boxShadow: '0 4px 6px rgba(231, 76, 60, 0.4)',
              transition: 'background-color 0.3s ease, transform 0.2s ease',
              display: 'flex',
              alignItems: 'center'
            }}
            onMouseEnter={e => {
              e.currentTarget.style.backgroundColor = '#c0392b';
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = '0 6px 10px rgba(192, 57, 43, 0.6)';
            }}
            onMouseLeave={e => {
              e.currentTarget.style.backgroundColor = '#e74c3c';
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 4px 6px rgba(231, 76, 60, 0.4)';
            }}
            >
              <FaTimesCircle style={{ marginRight: '6px' }} />
              Remove from Sale
            </button>
          )}
        </div>
      ))}

      <h2>Credits for Sale</h2>
      {marketCredits.length === 0 && <p>No credits for sale found.</p>}
      {marketCredits.map(credit => (
        <div key={credit.credit_id} className="card credit-info" style={{ position: 'relative' }}>
          <p>Credit ID: {credit.credit_id}, Project ID: {credit.project_id}, Amount: {credit.amount}, Issuer: {credit.issuer_email}, Owner: {credit.owner_email}, Status: {credit.status}</p>
          {credit.owner_email !== userEmail && (
            <button onClick={() => handleBuy(credit)} className="button-margin-left" style={{
              backgroundColor: '#27ae60',
              color: 'white',
              border: 'none',
              padding: '8px 14px',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '14px',
              fontWeight: '600',
              boxShadow: '0 4px 6px rgba(39, 174, 96, 0.4)',
              transition: 'background-color 0.3s ease, transform 0.2s ease',
              display: 'flex',
              alignItems: 'center'
            }}
            onMouseEnter={e => {
              e.currentTarget.style.backgroundColor = '#1e8449';
              e.currentTarget.style.transform = 'translateY(-2px)';
              e.currentTarget.style.boxShadow = '0 6px 10px rgba(30, 132, 73, 0.6)';
            }}
            onMouseLeave={e => {
              e.currentTarget.style.backgroundColor = '#27ae60';
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 4px 6px rgba(39, 174, 96, 0.4)';
            }}
            >
              <FaShoppingCart style={{ marginRight: '6px' }} />
              Buy
            </button>
          )}
        </div>
      ))}
    </div>
  );
}

export default Marketplace;
